mapper.select:
select user_id, user_name, token
from members
where user_id = #{id} and user_password = #{password}

mapper.update:
Update members 

set user_password = #{password},
user_name = #{name},
email_id = #{emailId},
email_domain = #{emailDomain}
where user_id = #{id}

mapper.select:
select user_name, user_id, email_id, email_domain, join_date
from members
where user_id = #{id}

mapper.update:
update members
set token = #{token}
where user_id = #{id}

mapper.select:
select token
from members
where user_id = #{id}

mapper.update:
update members
set token = #{token, jdbcType=VARCHAR}
where user_id = #{id}
